import { generateFormComponent } from "./form-generator/index";
export * from "./form-generator/index";
export default generateFormComponent;

import * as z from "zod";
import { type OAuthProvider } from "@/lib/oauth-providers-config";
import { type AuthPluginsConfig } from "./lib/form-generator/types";

export interface FormField {
  type: "input" | "textarea" | "select" | "checkbox" | "radio" | "date" | "file";
  name: string;
  label: string;
  placeholder?: string;
  description?: string;
  required?: boolean;
  options?: Array<{ label: string; value: string }>;
  inputType?: "text" | "email" | "password" | "tel" | "url" | "number" | "date";
  accept?: string; // For file inputs
}

export interface FormStep {
  id: string;
  title: string;
  description?: string;
  fields: FormField[];
}

export interface FormTemplate {
  id: string;
  name: string;
  description: string;
  category: "authentication" | "contact" | "ecommerce" | "survey" | "profile" | "booking";
  schema: z.ZodObject<any>;
  defaultValues: Record<string, any>;
  fields: FormField[];
  steps?: FormStep[];
  oauthProviders?: OAuthProvider[];
  authPlugins?: AuthPluginsConfig;
}
