"use client";

import * as React from "react";
import type {
  Control,
  FieldPath,
  FieldValues,
  ControllerRenderProps,
} from "react-hook-form";
import { Controller } from "react-hook-form";
import { Label } from "@/components/ui/label";
import { Input } from "@/components/ui/input";
import { cn, getErrorMessage } from "@/registry/default/lib/form-utils";

export interface FormInputProps<
  TFieldValues extends FieldValues = FieldValues,
  TName extends FieldPath<TFieldValues> = FieldPath<TFieldValues>
> extends Omit<React.InputHTMLAttributes<HTMLInputElement>, "name"> {
  /**
   * React Hook Form control object
   */
  control: Control<TFieldValues>;

  /**
   * Field name (must match schema)
   */
  name: TName;

  /**
   * Label text
   */
  label?: string;

  /**
   * Description text shown below the input
   */
  description?: string;

  /**
   * Whether the field is required
   */
  required?: boolean;

  /**
   * Custom className for the container
   */
  containerClassName?: string;

  /**
   * Custom className for the label
   */
  labelClassName?: string;

  /**
   * Custom className for the description
   */
  descriptionClassName?: string;

  /**
   * Custom className for the error message
   */
  errorClassName?: string;

  /**
   * Show asterisk for required fields
   * @default true
   */
  showRequiredAsterisk?: boolean;
}

/**
 * Enhanced input component with React Hook Form integration
 * 
 * @example
 * ```tsx
 * import { useForm } from "react-hook-form";
 * import { zodResolver } from "@hookform/resolvers/zod";
 * import * as z from "zod";
 * import { FormInput } from "@/components/formcn/form-input";
 * 
 * const schema = z.object({
 *   email: z.string().email("Invalid email address"),
 * });
 * 
 * function MyForm() {
 *   const form = useForm({
 *     resolver: zodResolver(schema),
 *   });
 * 
 *   return (
 *     <FormInput
 *       control={form.control}
 *       name="email"
 *       label="Email Address"
 *       type="email"
 *       placeholder="Enter your email"
 *       required
 *     />
 *   );
 * }
 * ```
 */
export function FormInput<
  TFieldValues extends FieldValues = FieldValues,
  TName extends FieldPath<TFieldValues> = FieldPath<TFieldValues>
>({
  control,
  name,
  label,
  description,
  required,
  containerClassName,
  labelClassName,
  descriptionClassName,
  errorClassName,
  showRequiredAsterisk = true,
  className,
  type = "text",
  ...props
}: FormInputProps<TFieldValues, TName>) {
  return (
    <Controller
      control={control}
      name={name}
      render={({ field, fieldState }) => {
        const error = fieldState.error;
        const errorMessage = error ? getErrorMessage(error) : undefined;

        return (
          <div className={cn("space-y-2", containerClassName)}>
            {label && (
              <Label
                htmlFor={field.name}
                className={cn(
                  error && "text-destructive",
                  labelClassName
                )}
              >
                {label}
                {required && showRequiredAsterisk && (
                  <span className="text-destructive ml-1">*</span>
                )}
              </Label>
            )}

            <Input
              {...field}
              {...props}
              id={field.name}
              type={type}
              className={cn(
                error && "border-destructive focus-visible:ring-destructive",
                className
              )}
              aria-invalid={!!error}
              aria-describedby={
                error
                  ? `${field.name}-error`
                  : description
                  ? `${field.name}-description`
                  : undefined
              }
              onChange={(e) => {
                field.onChange(e);
                props.onChange?.(e);
              }}
              onBlur={(e) => {
                field.onBlur();
                props.onBlur?.(e);
              }}
            />

            {description && !error && (
              <p
                id={`${field.name}-description`}
                className={cn(
                  "text-sm text-muted-foreground",
                  descriptionClassName
                )}
              >
                {description}
              </p>
            )}

            {error && (
              <p
                id={`${field.name}-error`}
                className={cn(
                  "text-sm font-medium text-destructive",
                  errorClassName
                )}
                role="alert"
              >
                {errorMessage}
              </p>
            )}
          </div>
        );
      }}
    />
  );
}
